class SpecificationExtensionCtrlTest extends FixtureTestCase {

    /**
     * @var ExtensionCtrl
     */
    protected $object;

    public function setUp() {
        $this->object = new ExtensionCtrl('specification');
        parent::setUp();
    }

    public function testGetSpecification() {
        $spec = new Specification(1, 'Sample description', 'Title', 1, 12);
        /* @var $spec2 Specification */

        $spec2 = $this->object->get($spec);
        $this->assertEquals(1, $spec2->id);
        $this->assertEquals('Sample description', $spec2->desc);
        $this->assertEquals('Title', $spec2->title);
        $this->assertEquals(1, $spec2->versionId);
        $this->assertEquals(12, $spec2->authorId);
    }

}
